import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

const CONTRACT_ADDRESS = "0x79Ed2B138DaD3B73896e0d7c9116f9762b4ddb77"; // Replace with your actual contract address
const CONTRACT_ABI = [
    {
        "inputs": [
            {
                "internalType": "uint256",
                "name": "quantity",
                "type": "uint256"
            }
        ],
        "name": "mint",
        "outputs": [],
        "stateMutability": "payable",
        "type": "function"
    },
    // Add other functions from your ABI if needed
];

function MintNFT() {
    const [mintPrice, setMintPrice] = useState("0");
    const [quantity, setQuantity] = useState(1);
    const [status, setStatus] = useState("");
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [contract, setContract] = useState(null);

    useEffect(() => {
        const init = async () => {
            if (typeof window.ethereum !== 'undefined') {
                try {
                    await window.ethereum.request({ method: 'eth_requestAccounts' });
                    const provider = new ethers.BrowserProvider(window.ethereum);
                    const signer = await provider.getSigner();
                    const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);
                    
                    const price = await contract.mintPrice();
                    setMintPrice(ethers.formatEther(price));
                    setContract(contract);
                } catch (error) {
                    setStatus("User denied account access");
                    console.error(error);
                }
            } else {
                setStatus("Please install MetaMask!");
            }
        };
        init();
    }, []);
    
    const handleMint = async () => {
        if (contract) {
            try {
                setStatus("Minting...");
                const tx = await contract.mint(quantity, { value: ethers.parseEther((mintPrice * quantity).toString()) });
                await tx.wait();
                setStatus("Minting successful!");
            } catch (error) {
                setStatus(`Minting failed: ${error.message}`);
                console.error(error);
            }
        }
    };

    return (
        <div className="mint-container">
            <h1>Mint Your InkTinyCow</h1>
            <p>Current Price: {mintPrice} ETH</p>
            <label>
                Number to Mint:
                <input 
                    type="number" 
                    value={quantity} 
                    onChange={(e) => setQuantity(parseInt(e.target.value, 10))} 
                    min="1" max="20" 
                />
            </label>
            <br />
            <button onClick={handleMint}>Mint</button>
            <p>{status}</p>
        </div>
    );
}

export default MintNFT;
