import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './MintNFT.css';
import bigInt from 'big-integer';



const BASE_SEPOLIA_CHAIN_ID = 84532; // Chain ID for Base Sepolia
const BASE_SEPOLIA_RPC_URL = 'https://sepolia.base.org'; // RPC URL for Base Sepolia
const CONTRACT_ADDRESS = "0x0104A62c30Ec4Bc0De22240d20E27CA868aE6994"; // Replace with your actual contract address
const CONTRACT_ABI = [
    {
        "inputs": [{"internalType": "uint256", "name": "quantity", "type": "uint256"}],
        "name": "mint",
        "outputs": [],
        "stateMutability": "payable",
        "type": "function"
    },
    {
        "inputs": [],
        "name": "mintPrice",
        "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
        "stateMutability": "view",
        "type": "function"
    },
    {
        "inputs": [],
        "name": "totalSupply",
        "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
        "stateMutability": "view",
        "type": "function"

    }
];

function MintNFT() {
    const [mintPrice, setMintPrice] = useState("0");
    const [quantity, setQuantity] = useState(1);
    const [status, setStatus] = useState("Ready to Mint");
    const [totalSupply, setTotalSupply] = useState(0); // This will be the total minted
    const [maxSupply, setMaxSupply] = useState(3000); // Assuming max supply is 3000
    const [contract, setContract] = useState(null);

useEffect(() => {
    const init = async () => {
        if (typeof window.ethereum !== 'undefined') {
            try {
                await window.ethereum.request({ method: 'eth_requestAccounts' });

                const chainId = await window.ethereum.request({ method: 'eth_chainId' });
                if (parseInt(chainId, 16) !== BASE_SEPOLIA_CHAIN_ID) {
                    await window.ethereum.request({
                        method: 'wallet_switchEthereumChain',
                        params: [{ chainId: `0x${BASE_SEPOLIA_CHAIN_ID.toString(16)}` }],
                    });
                }

                const provider = new ethers.BrowserProvider(window.ethereum, BASE_SEPOLIA_CHAIN_ID);
                const signer = await provider.getSigner();
                const contract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);
                
                const price = await contract.mintPrice();
                setMintPrice(ethers.formatEther(price));
                
                // Since totalSupply is not available, you might simulate or track it elsewhere
                // Here's a placeholder, adjust according to your needs
                setTotalSupply(0); // Or fetch from some other source or state management
                setMaxSupply(3000); // This could be hardcoded or fetched from another source
                
                setContract(contract);
                } catch (error) {
                    if (error.code === 4902) {
                        try {
                            await window.ethereum.request({
                                method: 'wallet_addEthereumChain',
                                params: [
                                    {
                                        chainId: `0x${BASE_SEPOLIA_CHAIN_ID.toString(16)}`,
                                        chainName: 'Base Sepolia',
                                        nativeCurrency: {
                                            name: 'Ether',
                                            symbol: 'ETH',
                                            decimals: 18
                                        },
                                        rpcUrls: [BASE_SEPOLIA_RPC_URL],
                                        blockExplorerUrls: ['https://sepolia.basescan.org/']
                                    },
                                ],
                            });
                        } catch (addError) {
                            setStatus("Failed to add Base Sepolia network");
                            console.error(addError);
                        }
                    } else {
                        setStatus("User denied account access or network switch. Please try again.");
                        console.error(error);
                    }
                }
            } else {
                setStatus("Please install MetaMask!");
            }
        };
        init();
    }, []);

    const handleMint = async () => {
        if (contract) {
            try {
                setStatus("Minting...");
                const tx = await contract.mint(quantity, { value: ethers.parseEther((mintPrice * quantity).toString()) });
                await tx.wait();
                setStatus("Minting successful!");
                // Refresh total minted after minting
         
              
            } catch (error) {
                console.error("Minting error details:", {
                    reason: error.reason,
                    message: error.message,
                    data: error.data
                });
                setStatus(`Minting failed: ${error.reason || error.message}`);
            }
        }
    };

    return (
        <div className="mint-container">
            <div className="header">
                <img src='https://raw.seadn.io/files/6af1acf660826d34fe3853265ca3c546.svg' alt="Ink Tiny Cow" className="logo" />
                <h1>Onchain Ink Tiny Cows</h1>
            </div>
            <div className="info-section">
                <p>Total Supply: {maxSupply}</p>
                <p>Total Minted: {totalSupply} / {maxSupply}</p>
                <p>Price - {mintPrice} ETH</p>
                <p>Contract: {CONTRACT_ADDRESS}</p>
                <p>Bridge to Ink: <a href="https://your-bridge-link.com" target="_blank" rel="noopener noreferrer">RelayLink</a></p>
            </div>
            <div className="mint-section">
                <input 
                    type="number" 
                    value={quantity} 
                    onChange={(e) => setQuantity(parseInt(e.target.value, 10))} 
                    min="1" max="20" 
                />
                <button onClick={handleMint}>Mint NFT</button>
            </div>
            <p className="status">{status}</p>
        </div>
    );
}

export default MintNFT;
